import React, { useState, useContext, useEffect } from "react";
import "./navbars.css";
// import "./NavBar.css";
import { Link } from "react-router-dom";
import LinkButton from "../LinkButton/LinkButton";
import { AuthContext } from "../../Context/AuthContext";

const NavBar = () => {
  const safari = navigator.userAgent.indexOf("Safari") !== -1 ? true : false;
  const [isOpen, setIsOpen] = useState(false);

  // const navwidth = safari ? "650px" : "650px";
  const navwidth = "400px";

  const authContext = useContext(AuthContext);
  useEffect(() => {
    // console.log(authContext.googleUser, "Changes");
  }, [authContext.googleUser]);

  return (
    <div className="Navbar">
      <Link
        to="./"
        className="nav-logo"
        style={{
          textDecoration: "none",
          fontSize: "24px",
          color: "pink",
          fontWeight: "bold",
          coursor: "pointer",
        }}
      >
        <span className="nav-logo">XRSim</span>
      </Link>

      <div className={`nav-items ${isOpen && "open"}`}>
        <Link onClick={() => setIsOpen(!isOpen)} to="/">
          Home
        </Link>
        <Link onClick={() => setIsOpen(!isOpen)} to="/services">
          Services
        </Link>
        <Link onClick={() => setIsOpen(!isOpen)} to="/blogs">
          Blogs
        </Link>
        <Link onClick={() => setIsOpen(!isOpen)} to="/aboutus">
          About Us
        </Link>

        <Link onClick={() => setIsOpen(!isOpen)} to="/contact">
          Contact Us
        </Link>
        {/* <Link onClick={() => setIsOpen(!isOpen)} to="/feedback">
          Feedback
        </Link> */}
        {authContext.googleUser.imageUrl !== "" ? (
          <>
            <img
              className="navbar-img"
              src={authContext.googleUser.imageUrl}
            ></img>
            <svg
              style={{ marginLeft: "20px", cursor: "pointer" }}
              onClick={() => {
                authContext.setGoogleUser({
                  name: "",
                  email: "",
                  imageUrl: "",
                  tokenID: "",
                });
                authContext.setIsAuthenticated(false);
                localStorage.clear();
              }}
              stroke="currentColor"
              fill="white"
              stroke-width="0"
              viewBox="0 0 512 512"
              height="1em"
              width="1em"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path d="M497 273L329 441c-15 15-41 4.5-41-17v-96H152c-13.3 0-24-10.7-24-24v-96c0-13.3 10.7-24 24-24h136V88c0-21.4 25.9-32 41-17l168 168c9.3 9.4 9.3 24.6 0 34zM192 436v-40c0-6.6-5.4-12-12-12H96c-17.7 0-32-14.3-32-32V160c0-17.7 14.3-32 32-32h84c6.6 0 12-5.4 12-12V76c0-6.6-5.4-12-12-12H96c-53 0-96 43-96 96v192c0 53 43 96 96 96h84c6.6 0 12-5.4 12-12z"></path>
            </svg>
          </>
        ) : null}
      </div>
      <div
        className={`nav-toggle ${isOpen && "open"}`}
        onClick={() => setIsOpen(!isOpen)}
      >
        <div className="bar"></div>
      </div>
    </div>
  );
};

export default NavBar;



